<!DOCTYPE html>
<html dir="ltr">
<head>
    <link href="url(bed681bde82a8ec7041d18c69517d8ff_wow_high_rez_icon_by_jocpocd39jgl5.png)" rel="icon" type="image/x-icon" />
	<script>
    var themeHasJQuery = !!window.jQuery;
</script>
<script type="text/javascript" src="@(assets.js)/jquery.js?1.0.755"></script>
<script>
    window._$ = jQuery.noConflict(themeHasJQuery);
</script>
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
<link rel="stylesheet" href="@(assets.css)/bootstrap.css?1.0.755" media="screen" />
<script type="text/javascript" src="@(assets.js)/bootstrap.min.js?1.0.755"></script>
<!--[if lte IE 9]>
<link rel="stylesheet" href="@(assets.css)/layout.ie.css?1.0.755">
<script src="@(assets.js)/layout.ie.js?1.0.755"></script>
<![endif]-->
<link class="data-control-id-9" href='//fonts.googleapis.com/css?family=Open+Sans:300,300italic,regular,italic,600,600italic,700,700italic,800,800italic&subset=latin' rel='stylesheet' type='text/css'>
<script type="text/javascript" src="@(assets.js)/layout.core.js"></script>
<script src="@(assets.js)/CloudZoom.js?1.0.755"></script>

    <title>@(page.titleInBrowser)</title>
	<link rel="stylesheet" href="@(assets.css)/style.css?1.0.755">
	<script src="@(assets.js)/script.js?1.0.755"></script>
    <meta charset="utf-8">


    @if (page.customHead) { @(page.customHead) }
    @if (page.customCss) { <style>@(page.customCss)</style> }
</head>
<body class="data-control-id-55 bootstrap bd-body-5  bd-pagebackground bd-margins">
    <header class="data-control-id-936270 bd-headerarea-1 bd-margins">
        <section class="data-control-id-2127137 bd-section-4 bd-page-width bd-tagstyles " id="section4" data-section-title="WoWGame">
    <div class="bd-container-inner bd-margins clearfix">
        <div class="data-control-id-2126903 bd-layoutbox-3 bd-background-width  bd-no-margins clearfix">
    <div class="bd-container-inner">
        <a class="data-control-id-2126912 bd-logo-2 bd-imagescaling bd-imagescaling-5" href="">
    <img class="data-control-id-2126910 bd-imagestyles" src="url(a878561ff76695bff9e369d26b0ee61c_home.png)">
</a>

		@if (typeof pages !== 'undefined' && pages.length) {

    <nav class="data-control-id-754 bd-hmenu-1" data-responsive-menu="true" data-responsive-levels="expand on click">

            <div class="data-control-id-519297 bd-responsivemenu-11 collapse-button">
    <div class="bd-container-inner">
        <div class="bd-menuitem-4 data-control-id-2157685">
            <a  data-toggle="collapse"
                data-target=".bd-hmenu-1 .collapse-button + .navbar-collapse"
                href="#" onclick="return false;">
                    <span>Menu</span>
            </a>
        </div>
    </div>
</div>
            <div class="navbar-collapse collapse">

            <div class="data-control-id-171385 bd-horizontalmenu-108 clearfix">
                <div class="bd-container-inner">

                    <ul class="data-control-id-171386 bd-menu-100 nav nav-pills navbar-left">
                        @{
                            var pageParents = page.parents ? page.parents : [];
                            var filterItems = function (items) {
                                return items.filter(function (i) {
                                    return i.showInHMenu;
                                });
                            };

                            var inParents = function (id) {
                                return pageParents.some(function (i) {
                                    return i.id === id;
                                });
                            };
                        }

                        @function subMenuBefore(level, hasMegamenu) {
                            <div class="bd-menu-84-popup @if (hasMegamenu && level === 3) { bd-megamenu-popup }">
                            @if (hasMegamenu && level === 2) {
                                <div class="bd-menu-84 bd-no-margins bd-mega-grid data-control-id-2157706 bd-grid-98">
                                    <div class="container-fluid">
                                        <div class="separated-grid row">
                            } else {
                                <ul class="data-control-id-171405 bd-menu-84 bd-no-margins">
                            }
                        }

                        @function subMenuAfter(level, hasMegamenu) {
                            @if (hasMegamenu && level === 2) {
                                        </div>
                                    </div>
                                </div>
                            } else {
                                </ul>
                            }
                            </div>
                        }

                        @function subMenuItemBefore(item, level, hasMegamenu) {
                            @{
                                var additionalClass = 'bd-sub-item';
                                if (hasMegamenu) {
                                    if (level === 2) {
                                        var responsive = {
                                            lg: item.columns_lg || '',
                                            md: item.columns_md || '',
                                            sm: item.columns_sm || '',
                                            xs: item.columns_xs || ''
                                        };

                                        var responsiveClasses = '',
                                            lastMode = '';

                                        for (var mode in responsive) {
                                            if ('xs' === mode) break;
                                            lastMode = mode;
                                            var value = responsive[mode];
                                            if (value) {
                                                responsiveClasses += ' col-' + mode + '-' + Math.round(12 / value);
                                            }
                                        }

                                        if (!responsiveClasses && lastMode) {
                                            for (var i = item.children.length; i <= 12; i++) {
                                                if (i && (12 % i === 0)) {
                                                    responsiveClasses += ' col-' + lastMode + '-' + Math.round(12 / i);
                                                    break;
                                                }
                                            }
                                        }

                                        additionalClass += ' bd-mega-item data-control-id-2157696 bd-menuitem-94';
                                        additionalClass += ' separated-item-90 ' + responsiveClasses;
                                    } else if (level === 3) {
                                        additionalClass += ' bd-mega-item data-control-id-2157720 bd-menuitem-96';
                                    }
                                }
                            }
                            @if (hasMegamenu && level === 2) {
                                <div class="data-control-id-171406 bd-menuitem-82 @(additionalClass)">
                                    <div class="data-control-id-2157708 bd-griditem-90 bd-grid-item">
                            } else {
                                <li class="data-control-id-171406 bd-menuitem-82 bd-sub-item @(additionalClass)">
                            }
                        }

                        @function subMenuItemAfter(level, hasMegamenu) {
                            @if (hasMegamenu && level === 2) {
                                    </div>
                                </div>
                            } else {
                                </li>
                            }
                        }

                        @function renderMenuItem(item) {
                            @{
                                var isActive = inParents(item.id) || page.id === item.id,
                                    itemHref = item.customUrl || (pathToRoot + item.filePath);
                            }
                            <a @if (isActive) { class="active" } href="@(itemHref)" @if (item.target) { target="@(item.target)" }>@(item.caption)</a>
                        }

                        @function renderHMenuLevels(item, level, hasMegamenu) {
                            @{ var items = filterItems(item.children); }
                            @if (items.length) {

                                @(subMenuBefore(level, hasMegamenu))

                                @for (var i = 0; i < items.length; i++) {
                                    @{ var subItem = items[i]; }

                                    @(subMenuItemBefore(item, level, hasMegamenu))

                                    @(renderMenuItem(subItem))

                                    @if (item.children) {
                                        @(renderHMenuLevels(subItem, level + 1, hasMegamenu))
                                    }

                                    @(subMenuItemAfter(level, hasMegamenu))
                                }

                                @(subMenuAfter(level, hasMegamenu))
                            }
                        }

                        @{ var items = filterItems(pages); }
                        @for (var i = 0; i < items.length; i++) {
                            @{
                                var levels = 'expand on hover';
                                var item = items[i];

                                var hasMegamenu = levels === 'megamenu' && !!item.children && item.children.some(function (child) {
                                    return child.children && child.children.length && !item.megamenu;
                                });
                            }
                            <li class="data-control-id-171387 bd-menuitem-80
                                        @if (item.children && filterItems(item.children).length) { bd-submenu-icon-only }
                                        bd-toplevel-item
                                        @if (hasMegamenu) {  }
                                        "
                                    @if (hasMegamenu) {
                                        data-mega-width="@(item.megamenuWidth ? item.megamenuWidth : 'sheet')"
                                        data-mega-width-value="@(item.customMenuWidth ? item.customMenuWidth : '600')"
                                    }>

                                @(renderMenuItem(item))

                                @if (item.children) {
                                    @(renderHMenuLevels(item, 2, hasMegamenu))
                                }
                            </li>
                        }
                    </ul>

                </div>
            </div>


            </div>
    </nav>

}
    </div>
</div>
    </div>
</section>
</header>

		<div class="data-control-id-1147717 bd-content-1">

    @{
    var _isBlog = page.storage === 'page',
        _posts = _isBlog ? posts : [ page ];
}
<div class="data-control-id-1244 bd-blog-1">
    <div class="bd-container-inner">

        @if (_isBlog) {

        }

        <div class="data-control-id-1090 bd-grid-4 bd-margins">
            <div class="separated-grid row">

                    @for(var j = 0; j < _posts.length; j++) {
                        @{
                            var _post = _posts[j],
                                _pathToPost = _isBlog ? pathToRoot + _post.filePath : '#';
                        }
                        <div class="separated-item-18 col-md-12 ">

                            <div class="bd-griditem-18">
                                <article class="data-control-id-1166 bd-article-2 clearfix">
    <h2 class="data-control-id-1007 bd-postheader-2">
    <a href="@(_pathToPost)">
        @(_post.caption)
    </a>
</h2>

		<div class="data-control-id-1024989 bd-layoutbox-2 bd-no-margins clearfix">
    <div class="bd-container-inner">
        <div class="data-control-id-1017 bd-posticondate-2 bd-no-margins">
    <span class="data-control-id-1016 bd-icon bd-icon-34"><span>@(_post.date)</span></span>
</div>

		<div class="data-control-id-1026 bd-posticonauthor-3 bd-no-margins">
    <a class="data-control-id-1025 bd-icon bd-icon-36"><span>@(_post.author || 'admin')</span></a>
</div>
    </div>
</div>

		<div class="data-control-id-2131630 bd-postimage-11">
    <img class="data-control-id-2131629 bd-imagestyles" @if (_post.image) { src="url(@(_post.image))" }/>
</div>

		<div class="data-control-id-234738 bd-postcontent-8 bd-tagstyles bd-custom-blockquotes bd-contentlayout-offset clearfix">
    @if (_isBlog) {
        @{
            var postContent = BD.Builder.removeDataId(_post.content || '');
            var sliceEnd = postContent.indexOf('<!-- export:postcut -->');
            var cuttedContent = postContent.slice(0, sliceEnd === -1 ? undefined : sliceEnd);
        }
        @(parse(cuttedContent))
    } else {
        <export:content>
    }
</div>

		<div class="data-control-id-1025097 bd-layoutbox-4 bd-no-margins clearfix">
    <div class="bd-container-inner">
        <a class="bd-postreadmore-4 bd-button-11 data-control-id-1002005" href="@(_pathToPost)">Continue reading...</a>
    </div>
</div>
</article>
                                @if (!_isBlog && _post.comments) {
                                    <div class="data-control-id-1239 bd-comments-1">
    <div class="bd-container-inner">
        <div class="data-control-id-1198 bd-container-15 bd-tagstyles bd-custom-blockquotes">
            <h2>Comments</h2>
        </div>
        @function renderComments(comments) {
            <ul>
            @for (var i = 0; i < comments.length; i++) {
                @{ var _comment = comments[i]; }
                <li>
                    <div class="data-control-id-1200 bd-comment-1">
    <div class="data-control-id-1086 bd-layoutcontainer-15 bd-columns bd-no-margins">
    <div class="bd-container-inner">
        <div class="container-fluid">
            <div class="row ">
                <div class="data-control-id-1082 bd-columnwrapper-32 
 col-md-1">
    <div class="bd-layoutcolumn-32 bd-no-margins bd-column" ><div class="bd-vertical-align-wrapper"><div class="data-control-id-1074 bd-commentavatar-1">
    <img class="data-control-id-1066 bd-imagestyles" src="" style="height: 50px; width: 50px">
</div></div></div>
</div>

		<div class="data-control-id-1084 bd-columnwrapper-33 
 col-md-11">
    <div class="bd-layoutcolumn-33 bd-column" ><div class="bd-vertical-align-wrapper"><div class="data-control-id-1065 bd-commentmetadata-1">
    <a href="#">@(_comment.author)</a> on @(_comment.date)
</div>

		<div class="data-control-id-1075 bd-commenttext-1">
    @(_comment.content)
</div>

		<div class="data-control-id-1080 bd-commentreply-1">
    <input type="submit" name="submit" class="data-control-id-1079 bd-button" value="Reply">
</div></div></div>
</div>
            </div>
        </div>
    </div>
</div>
</div>
                    @if (_comment.children) {
                        @(renderComments(_comment.children))
                    }
                </li>
            }
            </ul>
        }
        @(renderComments(_post.comments))
        <div  class="data-control-id-1238 bd-commentsform-1">
    <div class="data-control-id-1232 bd-container-16 bd-tagstyles bd-custom-blockquotes">
        <h2>Add comment </h2>
    </div>
    <form action="#" method="post">
        <p>
            <label for="author" class="data-control-id-190269 bd-bootstraplabel">Author</label>
            <input class="data-control-id-1233 bd-bootstrapinput form-control" id="author" name="author" type="text" value="" size="30" style="width: 255px">
        </p>
        <p>
            <label for="email" class="data-control-id-190269 bd-bootstraplabel">Email</label>
            <input class="data-control-id-1233 bd-bootstrapinput form-control" id="email" name="email" type="email" value="" size="30" style="width: 255px">
        </p>
        <p>
            <label for="comment" class="data-control-id-190269 bd-bootstraplabel">Message</label>
            <textarea class="data-control-id-1233 bd-bootstrapinput form-control" id="comment" name="comment" cols="45" rows="8" aria-required="true" style="width: 270px"></textarea>
        </p>
        <p>
            <input name="submit" class="data-control-id-1237 bd-button" type="submit" value="Submit">
        </p>
    </form>
</div>
    </div>
</div>
                                }
                            </div>
                        </div>
                    }
            </div>
        </div>
        @if (_isBlog) {

                <div class="data-control-id-1128 bd-blogpagination-1">
    <ul class="data-control-id-1127 bd-pagination-12 pagination">
    <li class="data-control-id-1126 bd-paginationitem-12 disabled"><a href="#">Prev</a></li>
<li class="data-control-id-1126 bd-paginationitem-12 active"><a href="#">1</a></li>
<li class="data-control-id-1126 bd-paginationitem-12"><a href="#">2</a></li>
<li class="data-control-id-1126 bd-paginationitem-12"><a href="#">3</a></li>
<li class="data-control-id-1126 bd-paginationitem-12"><a href="#">4</a></li>
<li class="data-control-id-1126 bd-paginationitem-12"><a href="#">5</a></li>
<li class="data-control-id-1126 bd-paginationitem-12"><a href="#">Next</a></li>
</ul>
</div>
        }
    </div>
</div>
</div>

		<footer class="data-control-id-936277 bd-footerarea-1 bd-margins">
        <p class="data-control-id-2163887 bd-textblock-17 bd-content-element">
    Copyright: WoWGame 2017
<br><br>Content is available under&nbsp;<a href="https://creativecommons.org/licenses/by-sa/3.0/">CC BY-SA <span style="background-image: none;">3.0</span>&nbsp;</a>unless otherwise noted.<br>World of Warcraft content and materials are trademarks and copyrights of Blizzard or its licensors. All rights reserved. This site is a part of WoWGaming.org and is not affiliated with Blizzard.<br>
</p>


</footer>

		<div data-smooth-scroll data-animation-time="250" class="data-control-id-520690 bd-smoothscroll-3"><a href="#" class="data-control-id-2787 bd-backtotop-1 ">
    <span class="bd-icon-67 bd-icon data-control-id-2786"></span>
</a></div>
</body>
</html>
